                      </Heading>
                      <p>New Batch Requested for the above course</p>
                      {/* <p>Batch - {course?.batch}</p>
                      <p>Time - {course?.time}</p> */}

                      {/* <p>Price - &#x20b9;{course?.selectedBatchPrice}</p> */}
                    </div>
                    <div className="notification-status">
                      <Badge dot />
                    </div>
                  </div>
                </div>
                {/* </Link> */}
