/* eslint-disable no-shadow */
import React, { Component, useState, useEffect } from "react";
import { Layout, Button, Row, Col } from "antd";
import FeatherIcon from "feather-icons-react";
import { NavLink, Link } from "react-router-dom";
import { Scrollbars } from "react-custom-scrollbars";
import { ThemeProvider } from "styled-components";
import { connect } from "react-redux";
import propTypes from "prop-types";
import MenueItems from "./MenueItems";
import TopMenu from "./TopMenu";
import "./style.css";
import {
  Div,
  SmallScreenAuthInfo,
  SmallScreenSearch,
  TopMenuSearch,
} from "./style";
import HeaderSearch from "../components/header-search/header-search";
import AuthInfo from "../components/utilities/auth-info/info";
import { Menu } from "antd";
import { Fragment } from "react";
import axiosInstance from "../config/axoisconfig";
const { darkTheme } = require("../config/theme/themeVariables");

const { Header, Footer, Sider, Content } = Layout;
// const { darkMode } = config;
const { SubMenu } = Menu;

const ThemeLayout = (WrappedComponent, type) => {
  class LayoutComponent extends Component {
    constructor(props) {
      super(props);
      this.state = {
        collapsed: false,
        isLoading: false,
        hide: true,
        searchHide: true,
        activeSearch: false,
        categories: [],
        subcategories: [],
        headcatlist: [],
        menuList: [],
      };
      this.updateDimensions = this.updateDimensions.bind(this);
    }

    componentDidMount() {
      let categories = [];
      let subcategories = [];
      const categoryURL = "https://api.esculae.com/api/v1/course/category";
      const subCategoryURL =
        "https://api.esculae.com/api/v1/course/sub-category";
      axiosInstance
        .all([
          axiosInstance.get(categoryURL),
          axiosInstance.get(subCategoryURL),
        ])
        .then(
          axiosInstance.spread((...responses) => {
            categories = responses[0].data;
            subcategories = responses[1].data;
            this.setState({ categories, subcategories });
            let list = [];
            categories.data.map((category, idx) => {
              list.push({
                category,
                subCategories: [],
              });
              console.log("dadasasdlist", list);
              subcategories.data.map((subcategory) => {
                if (
                  subcategory.CategoryId === category.id &&
                  subcategory.is_active &&
                  category.is_active
                ) {
                  list[idx].subCategories &&
                    list[idx].subCategories.push({
                      subcategory,
                    });
                }
              });
            });
            this.setState({ menuList: list });
          })
        )
        .catch((errors) => {
          console.log("Error in getting api response", errors);
        });
      // window.addEventListener("resize", this.updateDimensions);
      this.updateDimensions();
    }

    componentWillUnmount() {
      // window.removeEventListener("resize", this.updateDimensions);
    }

    updateDimensions() {
      this.setState({
        collapsed: window.innerWidth <= 1200 && true,
      });
    }

    render() {
      const { collapsed, hide, searchHide, activeSearch } = this.state;
      const { ChangeLayoutMode, rtl, topMenu } = this.props;

      const left = !rtl ? "left" : "right";
      const darkMode = ChangeLayoutMode;
      const toggleCollapsed = () => {
        this.setState({
          collapsed: !collapsed,
        });
      };

      const toggleCollapsedMobile = () => {
        if (window.innerWidth <= 990) {
          this.setState({
            collapsed: !collapsed,
          });
        }
      };

      const onShowHide = () => {
        this.setState({
          hide: !hide,
          searchHide: true,
        });
      };

      const toggleSearch = () => {
        this.setState({
          activeSearch: !activeSearch,
        });
      };

      const handleSearchHide = (e) => {
        e.preventDefault();
        this.setState({
          searchHide: !searchHide,
          hide: true,
        });
      };

      const footerStyle = {
        padding: "20px 30px 18px",
        color: "rgba(0, 0, 0, 0.65)",
        fontSize: "14px",
        background: "rgba(255, 255, 255, .90)",
        width: "100%",
        boxShadow: "0 -5px 10px rgba(146,153,184, 0.05)",
      };

      const SideBarStyle = {
        margin: "63px 0 0 0",
        padding: "15px 15px 55px 15px",
        overflowY: "auto",
        height: "100vh",
        position: "fixed",
        [left]: 0,
        zIndex: 998,
      };

      const renderView = ({ style, ...props }) => {
        const customStyle = {
          marginRight: "auto",
          [rtl ? "marginLeft" : "marginRight"]: "-17px",
        };
        return <div {...props} style={{ ...style, ...customStyle }} />;
      };

      const renderThumbVertical = ({ style, ...props }) => {
        const { ChangeLayoutMode } = this.props;
        const thumbStyle = {
          borderRadius: 6,
          backgroundColor: ChangeLayoutMode ? "#ffffff16" : "#F1F2F6",
          [left]: "2px",
        };
        return <div style={{ ...style, ...thumbStyle }} props={props} />;
      };

      const renderTrackVertical = () => {
        const thumbStyle = {
          position: "absolute",
          width: "6px",
          transition: "opacity 200ms ease 0s",
          opacity: 0,
          [rtl ? "left" : "right"]: "2px",
          bottom: "2px",
          top: "2px",
          borderRadius: "3px",
        };
        return <div style={thumbStyle} />;
      };

      const renderThumbHorizontal = ({ style, ...props }) => {
        const { ChangeLayoutMode } = this.props;
        const thumbStyle = {
          borderRadius: 6,
          backgroundColor: ChangeLayoutMode ? "#ffffff16" : "#F1F2F6",
        };
        return <div style={{ ...style, ...thumbStyle }} props={props} />;
      };

      return (
        <Div darkMode={darkMode}>
          <Layout className="layout">
            <Header
              style={{
                position: "fixed",
                width: "100%",
                height: window.innerWidth <= 991 ? "70px" : "100px",
                top: 0,
                [!rtl ? "left" : "right"]: 0,
              }}
            >
              <Row>
                <Col
                  lg={!topMenu ? 4 : 3}
                  sm={6}
                  xs={12}
                  className="align-center-v navbar-brand"
                >
                  {!topMenu || window.innerWidth <= 991 ? (
                    <Button type="link" onClick={toggleCollapsed}>
                      <img
                        src={require(`../static/img/icon/${
                          collapsed ? "right.svg" : "left.svg"
                        }`)}
                        alt="menu"
                      />
                    </Button>
                  ) : null}
                  <Link
                    className={
                      topMenu && window.innerWidth > 991
                        ? "striking-logo top-menu"
                        : "striking-logo"
                    }
                    to="/home"
                  >
                    <img
                      src={
                        !darkMode
                          ? require(`../static/img/newLogo.png`)
                          : require(`../static/img/newLogo.png`)
                      }
                      alt=""
                    />
                  </Link>
                </Col>
                <Col lg={!topMenu ? 14 : 15} md={8} sm={0} xs={0}>
                  {topMenu && window.innerWidth > 991 ? (
                    <TopMenu />
                  ) : (
                    <Fragment></Fragment>
                    // <HeaderSearch rtl={rtl} darkMode={darkMode} />
                  )}
                </Col>
                <Col lg={6} md={10} sm={0} xs={0}>
                  {topMenu && window.innerWidth > 991 ? (
                    <></>
                  ) : (
                    // <TopMenuSearch>
                    //   <div className="top-right-wrap d-flex">
                    //     <Link
                    //       className={`${
                    //         activeSearch
                    //           ? "search-toggle active"
                    //           : "search-toggle"
                    //       }`}
                    //       onClick={() => {
                    //         toggleSearch();
                    //       }}
                    //       to="#"
                    //     >
                    //       <FeatherIcon icon="search" />
                    //       <FeatherIcon icon="x" />
                    //     </Link>
                    //     <div
                    //       className={`${
                    //         activeSearch
                    //           ? "topMenu-search-form show"
                    //           : "topMenu-search-form"
                    //       }`}
                    //     >
                    //       <form action="">
                    //         <span className="search-icon">
                    //           <FeatherIcon icon="search" />
                    //         </span>
                    //         <input type="text" name="search" />
                    //       </form>
                    //     </div>
                    //     <AuthInfo />
                    //   </div>
                    // </TopMenuSearch>
                    <AuthInfo />
                  )}
                </Col>
                <Col md={0} sm={18} xs={12}>
                  <>
                    <div className="mobile-action">
                      <Link
                        className="btn-search"
                        onClick={handleSearchHide}
                        to="#"
                      >
                        {searchHide ? (
                          <FeatherIcon icon="search" />
                        ) : (
                          <FeatherIcon icon="x" />
                        )}
                      </Link>
                      <Link className="btn-auth" onClick={onShowHide} to="#">
                        <FeatherIcon icon="more-vertical" />
                      </Link>
                    </div>
                  </>
                </Col>
              </Row>
              <Layout>
                {window.innerWidth > 991 && (
                  <>
                    <Row
                      style={{
                        clear: "both",
                        marginRight: "-30px",
                        marginTop: "-1px",
                      }}
                    >
                      <Menu
                        onClick={this.handleClick}
                        style={{
                          width: "100%",
                          paddingLeft: "20px",
                          paddingRight: "20px",
                        }}
                        mode="horizontal"
                      >
                        {this.state.menuList.length > 0 &&
                          this.state.menuList.map((cat) =>
                            cat.subCategories.length <= 0 ? (
                              <>
                                <Menu.Item key={cat.category.id}>
                                  {cat.category.name}
                                </Menu.Item>
                              </>
                            ) : (
                              <>
                                <SubMenu
                                  key={cat.category.id}
                                  title={cat.category.name}
                                >
                                  {cat.subCategories.map((subcat) => (
                                    <>
                                      <Menu.Item key={subcat.subcategory.id}>
                                        {subcat.subcategory.name}
                                      </Menu.Item>
                                    </>
                                  ))}
                                </SubMenu>
                              </>
                            )
                          )}
                      </Menu>
                    </Row>
                  </>
                )}
              </Layout>
            </Header>
            <div className="header-more">
              <Row>
                <Col md={0} sm={24} xs={24}>
                  <div className="small-screen-headerRight">
                    <SmallScreenSearch hide={searchHide} darkMode={darkMode}>
                      <HeaderSearch rtl={rtl} />
                    </SmallScreenSearch>
                    <SmallScreenAuthInfo hide={hide} darkMode={darkMode}>
                      <AuthInfo rtl={rtl} />
                    </SmallScreenAuthInfo>
                  </div>
                </Col>
              </Row>
            </div>
            <Layout>
              {window.innerWidth <= 991 ? (
                <Layout>
                  <ThemeProvider theme={darkTheme}>
                    <Sider
                      width={280}
                      style={SideBarStyle}
                      collapsed={collapsed}
                      theme={!darkMode ? "light" : "dark"}
                    >
                      <Menu
                        onClick={this.handleClick}
                        style={{ width: 256 }}
                        defaultSelectedKeys={["1"]}
                        defaultOpenKeys={["sub1"]}
                        mode="inline"
                      >
                        {/* <SubMenu key="sub2" title="Browse Categories"> */}

                        {this.state.menuList.length > 0 &&
                          this.state.menuList.map((cat) =>
                            cat.subCategories.length <= 0 ? (
                              <>
                                <Menu.Item key={cat.category.id}>
                                  {cat.category.name}
                                </Menu.Item>
                              </>
                            ) : (
                              <>
                                <SubMenu
                                  key={cat.category.id}
                                  title={cat.category.name}
                                >
                                  {cat.subCategories.map((subcat) => (
                                    <>
                                      <Menu.Item key={subcat.subcategory.id}>
                                        {subcat.subcategory.name}
                                      </Menu.Item>
                                    </>
                                  ))}
                                </SubMenu>
                              </>
                            )
                          )}
                      </Menu>
                    </Sider>
                  </ThemeProvider>
                </Layout>
              ) : null}

              {/* <Layout className="atbd-main-layout"> */}
              <Content style={{ paddingTop: "100px" }}>
                <WrappedComponent {...this.props} />
                <Footer className="admin-footer" style={footerStyle}>
                  <Row>
                    <Col md={12} xs={24}>
                      <span className="admin-footer__copyright">
                        {new Date().getFullYear()} © Bodhe
                      </span>
                    </Col>
                    <Col md={12} xs={24}>
                      <div className="admin-footer__links">
                        <NavLink to="#">About</NavLink>
                        <NavLink to="#">Team</NavLink>
                        <NavLink to="#">Contact</NavLink>
                      </div>
                    </Col>
                  </Row>
                </Footer>
              </Content>
              {/* </Layout> */}
            </Layout>
          </Layout>
        </Div>
      );
    }
  }

  const mapStateToProps = (state) => {
    return {
      ChangeLayoutMode: state.ChangeLayoutMode.data,
      rtl: state.ChangeLayoutMode.rtlData,
      topMenu: state.ChangeLayoutMode.topMenu,
    };
  };

  LayoutComponent.propTypes = {
    ChangeLayoutMode: propTypes.bool,
    rtl: propTypes.bool,
    topMenu: propTypes.bool,
    changeRtl: propTypes.func,
    changeLayout: propTypes.func,
    changeMenuMode: propTypes.func,
  };

  return connect(mapStateToProps)(LayoutComponent);
};
export default ThemeLayout;
